import React, { useState, useCallback, useEffect } from 'react';
import './ImageViewer.css';
import SideToolbar from '../SideToolbar/SideToolbar';
import Cropper from 'react-easy-crop';

function ImageViewer({ image, onClose, onSave }) {
    const [crop, setCrop] = useState({ x: 0, y: 0 });
    const [zoom, setZoom] = useState(1);
    const [imageSrc, setImageSrc] = useState(null);
    const [imageSize, setImageSize] = useState(null);
    const [croppedAreaPixels, setCroppedAreaPixels] = useState(null);
    const [brightness, setBrightness] = useState(100);
    const [contrast, setContrast] = useState(100);

    // Load the image when the component mounts
    useEffect(() => {
        if (image) {
            const reader = new FileReader();
            reader.onload = () => {
                setImageSrc(reader.result);
            };
            reader.readAsDataURL(image);
        }
    }, [image]);

    // Handle changes in crop
    const onCropChange = useCallback((crop) => {
        setCrop(crop);
    }, []);

    // Handle changes in zoom
    const onZoomChange = useCallback((zoom) => {
        setZoom(zoom);
    }, []);

    // Handle image load
    const onImageLoad = useCallback((image, pixelCrop) => {
        setImageSize({ width: image.naturalWidth, height: image.naturalHeight });
        setCroppedAreaPixels(pixelCrop);
    }, []);

    // Handle brightness change
    const handleBrightnessChange = (value) => {
        setBrightness(value); // Update brightness state
    };

    // Handle contrast change
    const handleContrastChange = (value) => {
        setContrast(value); // Update contrast state
    };

    // Handle save action
    // Handle save action
    const handleSave = () => {
        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');
        const img = new Image();
        img.onload = () => {
            canvas.width = img.width;
            canvas.height = img.height;
            ctx.filter = `brightness(${brightness}%) contrast(${contrast}%)`;
            ctx.drawImage(img, 0, 0);
            canvas.toBlob(blob => {
                const newImage = new File([blob], 'edited_image.jpeg', { type: 'image/jpeg' });
                onSave(newImage); // Pass the edited image data to the parent component
            }, 'image/jpeg', 1);
        };
        img.src = imageSrc;
    };


    return (
        <div className="image-viewer">
            <div className="controls">
                <button onClick={() => setZoom(zoom + 0.1)}>Zoom In</button>
                <button onClick={() => setZoom(zoom - 0.1)}>Zoom Out</button>
                <button onClick={handleSave}>Save</button>
                <button onClick={onClose}>Close</button>
            </div>
            <div className="image-container" style={{ filter: `brightness(${brightness}%) contrast(${contrast}%)` }}>
                {imageSrc && (
                    <Cropper
                        image={imageSrc}
                        crop={crop}
                        zoom={zoom}
                        aspect={4 / 3}
                        onCropChange={onCropChange}
                        onZoomChange={onZoomChange}
                        onCropComplete={onImageLoad}
                        style={{ width: '100%', height: '100%' }}
                    />
                )}
            </div>
            <SideToolbar
                onBrightnessChange={handleBrightnessChange}
                onContrastChange={handleContrastChange}
                onSave={handleSave}
            />
        </div>
    );
}

export default ImageViewer;
