import React, { useState, useEffect } from 'react';
import './ImageViewer.css';
import SideToolbar from "../SideToolbar/SideToolbar";

function ImageViewer({ image, onClose }) {
    const [scale, setScale] = useState(1);
    const [positionX, setPositionX] = useState(0);
    const [positionY, setPositionY] = useState(0);
    const [isDragging, setIsDragging] = useState(false);
    const [dragStartX, setDragStartX] = useState(0);
    const [dragStartY, setDragStartY] = useState(0);
    const [brightness, setBrightness] = useState(0);
    const [contrast, setContrast] = useState(0);
    const [modifiedImage, setModifiedImage] = useState(null);

    useEffect(() => {
        applyFilters();
    }, [brightness, contrast]);

    const handleZoomIn = () => {
        setScale(scale + 0.1);
    };

    const handleZoomOut = () => {
        if (scale > 0.1) {
            setScale(scale - 0.1);
        }
    };

    const handleMouseDown = (e) => {
        setIsDragging(true);
        setDragStartX(e.clientX - positionX);
        setDragStartY(e.clientY - positionY);
    };

    const handleMouseMove = (e) => {
        if (!isDragging) return;
        setPositionX(e.clientX - dragStartX);
        setPositionY(e.clientY - dragStartY);
    };

    const handleMouseUp = () => {
        setIsDragging(false);
    };

    const stopPropagation = (e) => {
        e.stopPropagation();
    };


    const applyFilters = () => {
        if (!image) return;

        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');
        canvas.width = image.width;
        canvas.height = image.height;

        const imageToDraw = modifiedImage ? modifiedImage : image; // Use modifiedImage if available, otherwise use the original image
        const img = new Image();
        img.src = imageToDraw;
        img.onload = () => {
            ctx.filter = `brightness(${brightness}%) contrast(${contrast}%)`;
            ctx.drawImage(img, 0, 0);
            const modifiedImageUrl = canvas.toDataURL();
            setModifiedImage(modifiedImageUrl);
        };
    };


    const handleBrightnessChange = (e) => {
        const value = parseInt(e.target.value);
        setBrightness(value);
    };

    const handleContrastChange = (e) => {
        const value = parseInt(e.target.value);
        setContrast(value);
    };

    const handleSave = () => {
        // Here, you can pass the modified image back to the ImageEditor
        onClose(modifiedImage);
    };

    return (
        <div className="image-viewer" onClick={stopPropagation}>
            <div className="controls">
                <button onClick={handleZoomIn}>Zoom In</button>
                <button onClick={handleZoomOut}>Zoom Out</button>
                <button onClick={onClose}>Close</button>
            </div>
            <div
                className="image-container"
                onMouseDown={handleMouseDown}
                onMouseMove={handleMouseMove}
                onMouseUp={handleMouseUp}
            >
                {image && (
                    <img
                        src={modifiedImage || URL.createObjectURL(image)}
                        alt="Zoomed Image"
                        className="zoomed-image"
                        style={{
                            transform: `scale(${scale}) translate(${positionX}px, ${positionY}px)`,
                            cursor: isDragging ? 'grabbing' : 'grab'
                        }}
                    />
                )}
            </div>
            {modifiedImage && (
                <SideToolbar
                    onBrightnessChange={handleBrightnessChange}
                    onContrastChange={handleContrastChange}
                    onSave={handleSave}
                />
            )}
        </div>
    );
}

export default ImageViewer;
