import React, { useRef, useEffect, useState } from 'react';
import './ImageViewer.css';
import SideToolbar from '../SideToolbar/SideToolbar';

function ImageViewer({ image, onClose, onSave }) {
    const imageCanvasRef = useRef(null);
    const drawingCanvasRef = useRef(null);
    const [imageContext, setImageContext] = useState(null);
    const [drawingContext, setDrawingContext] = useState(null);
    const [imageSrc, setImageSrc] = useState(null);
    const [brightness, setBrightness] = useState(100);
    const [contrast, setContrast] = useState(100);
    const [shapes, setShapes] = useState([]);
    const [selectedShapeIndex, setSelectedShapeIndex] = useState(null);
    const [isDragging, setIsDragging] = useState(false);
    const [dragStartX, setDragStartX] = useState(0);
    const [dragStartY, setDragStartY] = useState(0);
    const [isResizing, setIsResizing] = useState(false);
    const [resizeIndex, setResizeIndex] = useState(-1);
    const [resizeStartX, setResizeStartX] = useState(0);
    const [resizeStartY, setResizeStartY] = useState(0);
    const [prevMouseX, setPrevMouseX] = useState(0);
    const [prevMouseY, setPrevMouseY] = useState(0);

    const [penMode, setPenMode] = useState(false);
    const [rubberMode, setRubberMode] = useState(false);
    const [penColor, setPenColor] = useState('#000000');
    const [penThickness, setPenThickness] = useState(2);


    useEffect(() => {
        if (image) {
            const reader = new FileReader();
            reader.onload = () => {
                setImageSrc(reader.result);
            };
            reader.readAsDataURL(image);
        }
    }, [image]);

    useEffect(() => {
        const imageCanvas = imageCanvasRef.current;
        const ctx = imageCanvas.getContext('2d');
        setImageContext(ctx);
    }, []);

    useEffect(() => {
        const drawingCanvas = drawingCanvasRef.current;
        const ctx = drawingCanvas.getContext('2d');
        setDrawingContext(ctx);
    }, []);

    useEffect(() => {
        if (image && imageCanvasRef.current && drawingCanvasRef.current) {
            const reader = new FileReader();
            reader.onload = () => {
                setImageSrc(reader.result);
                const img = new Image();
                img.onload = () => {
                    const { width, height } = img;
                    const imageCanvas = imageCanvasRef.current;
                    const drawingCanvas = drawingCanvasRef.current;
                    const imageCtx = imageCanvas.getContext('2d');
                    const drawingCtx = drawingCanvas.getContext('2d');

                    // Set the width and height of the canvases
                    imageCanvas.width = width;
                    imageCanvas.height = height;
                    drawingCanvas.width = width;
                    drawingCanvas.height = height;

                    // Set the context variables
                    setImageContext(imageCtx);
                    setDrawingContext(drawingCtx);

                    // Draw the image on the imageCanvas
                    imageCtx.filter = `brightness(${brightness}%) contrast(${contrast}%)`;
                    imageCtx.drawImage(img, 0, 0, width, height);

                    // Redraw shapes on the drawingCanvas
                    shapes.forEach((shape, index) => {
                        drawShape(drawingCtx, shape, index === selectedShapeIndex, index === resizeIndex);
                    });
                };
                img.src = reader.result;
            };
            reader.readAsDataURL(image);
        }
    }, [image, brightness, contrast, shapes, selectedShapeIndex, resizeIndex]);

    const handleMouseDown = (e) => {
        const canvas = drawingCanvasRef.current;
        const rect = canvas.getBoundingClientRect();
        const mouseX = e.clientX - rect.left;
        const mouseY = e.clientY - rect.top;

        if (penMode) {
            setIsDragging(true);
            setPrevMouseX(mouseX);
            setPrevMouseY(mouseY);
        } else if (rubberMode) {
            const ctx = canvas.getContext('2d');
            ctx.globalCompositeOperation = 'destination-out';
            ctx.strokeStyle = 'rgba(0,0,0,0)';
            ctx.lineWidth = penThickness;
            setIsDragging(true);
            setPrevMouseX(mouseX);
            setPrevMouseY(mouseY);
        } else {
            let shapeSelected = false;
            shapes.forEach((shape, index) => {
                if (isPointInsideShape(mouseX, mouseY, shape)) {
                    setSelectedShapeIndex(index);
                    setIsDragging(true);
                    setDragStartX(mouseX - shape.x);
                    setDragStartY(mouseY - shape.y);
                    setIsResizing(isResizingHandle(mouseX, mouseY, shape));
                    if (isResizing) {
                        setResizeIndex(index);
                        setResizeStartX(mouseX - shape.x);
                        setResizeStartY(mouseY - shape.y);
                    }
                    shapeSelected = true;
                }
            });

            if (!shapeSelected) {
                setSelectedShapeIndex(null);
            }
        }
    };

    const handleMouseMove = (e) => {
        if (!isDragging || (penMode && !rubberMode && !isDragging)) return;

        const canvas = drawingCanvasRef.current;
        const rect = canvas.getBoundingClientRect();
        const mouseX = e.clientX - rect.left;
        const mouseY = e.clientY - rect.top;

        if (penMode) {
            drawingContext.strokeStyle = rubberMode ? '#FFFFFF' : penColor;
            drawingContext.lineWidth = penThickness;
            drawingContext.lineCap = 'round';
            drawingContext.beginPath();
            drawingContext.moveTo(prevMouseX, prevMouseY);
            drawingContext.lineTo(mouseX, mouseY);
            drawingContext.stroke();
            drawingContext.closePath();
            setPrevMouseX(mouseX);
            setPrevMouseY(mouseY);
        } else if (rubberMode) {
            drawingContext.globalCompositeOperation = 'destination-out';
            drawingContext.beginPath();
            drawingContext.arc(mouseX, mouseY, penThickness / 2, 0, 2 * Math.PI);
            drawingContext.fill();
        } else {
            const newShapes = [...shapes];
            const shape = newShapes[selectedShapeIndex];
            if (!isResizing) {
                shape.x = mouseX - dragStartX;
                shape.y = mouseY - dragStartY;
            } else {
                const dx = mouseX - shape.x - resizeStartX;
                const dy = mouseY - shape.y - resizeStartY;
                shape.width += dx;
                shape.height += dy;
                setResizeStartX(mouseX - shape.x);
                setResizeStartY(mouseY - shape.y);
            }
            setShapes(newShapes);
        }
    };

    const handleMouseUp = () => {
        setIsDragging(false);
        setIsResizing(false);
    };

    const isResizingHandle = (x, y, shape) => {
        return x >= shape.x + shape.width - 10 && x <= shape.x + shape.width &&
            y >= shape.y + shape.height - 10 && y <= shape.y + shape.height;
    };

    const drawShape = (ctx, shape, isSelected, isResizing) => {
        ctx.beginPath();
        ctx.rect(shape.x, shape.y, shape.width, shape.height);
        ctx.strokeStyle = isSelected ? 'red' : shape.color;
        ctx.lineWidth = shape.lineWidth;
        ctx.stroke();
    };

    const handleDeleteShape = () => {
        if (selectedShapeIndex !== null) {
            const newShapes = [...shapes];
            newShapes.splice(selectedShapeIndex, 1);
            setShapes(newShapes);
            setSelectedShapeIndex(null);
        }
    };

    const handleSave = () => {
        const imageCanvas = imageCanvasRef.current;
        const drawingCanvas = drawingCanvasRef.current;
        const mergedCanvas = document.createElement('canvas');
        const mergedCtx = mergedCanvas.getContext('2d');

        // Set the dimensions of the merged canvas
        mergedCanvas.width = imageCanvas.width;
        mergedCanvas.height = imageCanvas.height;

        // Draw the image canvas onto the merged canvas
        mergedCtx.drawImage(imageCanvas, 0, 0);

        // Draw the drawing canvas onto the merged canvas
        mergedCtx.drawImage(drawingCanvas, 0, 0);

        // Convert the merged canvas to a blob
        mergedCanvas.toBlob(blob => {
            onSave(blob);
        }, 'image/jpeg', 1);
    };

    const handleAddShape = (type) => {
        const newShapes = [...shapes];
        if (type === 'square') {
            newShapes.push({
                type: 'square',
                x: 100,
                y: 100,
                width: 50,
                height: 50,
                color: 'red',
                lineWidth: 2,
            });
        } else if (type === 'rectangle') {
            newShapes.push({
                type: 'rectangle',
                x: 200,
                y: 200,
                width: 80,
                height: 40,
                color: 'blue',
                lineWidth: 2,
            });
        }
        setShapes(newShapes);
    };

    const handleZoomIn = () => {
        if (imageContext && drawingContext) {
            const scaleFactor = 1.1;

            // Calculate the center point for zooming
            const centerX = imageCanvasRef.current.width / 2;
            const centerY = imageCanvasRef.current.height / 2;

            // Apply zoom transformation to the image canvas
            imageContext.clearRect(0, 0, imageCanvasRef.current.width, imageCanvasRef.current.height);
            imageContext.translate(centerX, centerY);
            imageContext.scale(scaleFactor, scaleFactor);
            imageContext.translate(-centerX, -centerY);
            imageContext.filter = `brightness(${brightness}%) contrast(${contrast}%)`;
            imageContext.drawImage(imageCanvasRef.current, 0, 0);

            // Apply zoom transformation to the drawing canvas
            drawingContext.clearRect(0, 0, drawingCanvasRef.current.width, drawingCanvasRef.current.height);
            drawingContext.translate(centerX, centerY);
            drawingContext.scale(scaleFactor, scaleFactor);
            drawingContext.translate(-centerX, -centerY);
            drawingContext.drawImage(drawingCanvasRef.current, 0, 0);
        }
    };

    const handleZoomOut = () => {
        if (imageContext && drawingContext) {
            const scaleFactor = 0.9;

            // Calculate the center point for zooming
            const centerX = imageCanvasRef.current.width / 2;
            const centerY = imageCanvasRef.current.height / 2;

            // Apply zoom transformation to the image canvas
            imageContext.clearRect(0, 0, imageCanvasRef.current.width, imageCanvasRef.current.height);
            imageContext.translate(centerX, centerY);
            imageContext.scale(scaleFactor, scaleFactor);
            imageContext.translate(-centerX, -centerY);
            imageContext.filter = `brightness(${brightness}%) contrast(${contrast}%)`;
            imageContext.drawImage(imageCanvasRef.current, 0, 0);

            // Apply zoom transformation to the drawing canvas
            drawingContext.clearRect(0, 0, drawingCanvasRef.current.width, drawingCanvasRef.current.height);
            drawingContext.translate(centerX, centerY);
            drawingContext.scale(scaleFactor, scaleFactor);
            drawingContext.translate(-centerX, -centerY);
            drawingContext.drawImage(drawingCanvasRef.current, 0, 0);
        }
    };


    const togglePenMode = () => {
        setPenMode(!penMode);
        setRubberMode(false); // Ensure rubber mode is turned off when switching to pen mode
    };

    const clearCanvas = () => {
        const canvas = drawingCanvasRef.current;
        const ctx = canvas.getContext('2d');
        ctx.clearRect(0, 0, canvas.width, canvas.height);
    };

    const handleToggleRubberMode = () => {
        setRubberMode(!rubberMode);
        setPenMode(false); // Ensure pen mode is turned off when switching to rubber mode
    };


    const isPointInsideShape = (x, y, shape) => {
        if (shape.type === 'square' || shape.type === 'rectangle') {
            return x >= shape.x && x <= (shape.x + shape.width) &&
                y >= shape.y && y <= (shape.y + shape.height);
        } else if (shape.type === 'circle') {
            const dx = x - shape.x;
            const dy = y - shape.y;
            return dx * dx + dy * dy <= shape.radius * shape.radius;
        }
        return false;
    };

    const handleTogglePenMode = () => {
        setPenMode(!penMode);
        setRubberMode(false);
    };


    return (
        <div className="image-viewer">
            <canvas
                ref={imageCanvasRef}
                style={{ position: 'absolute' }}
            />
            <canvas
                ref={drawingCanvasRef}
                onMouseDown={handleMouseDown}
                onMouseMove={handleMouseMove}
                onMouseUp={handleMouseUp}
                style={{ position: 'absolute' }}
            />
            <div className="controls">
                <button onClick={handleZoomIn}>Zoom In</button>
                <button onClick={handleZoomOut}>Zoom Out</button>
                <button onClick={handleSave}>Save</button>
                <button onClick={onClose}>Close</button>
                <button onClick={togglePenMode}>{penMode ? 'Switch to Eraser' : 'Switch to Pen'}</button>
                <button onClick={clearCanvas}>Clear Canvas</button>
                {selectedShapeIndex !== null && (
                    <button onClick={handleDeleteShape} style={{
                        position: 'absolute',
                        top: shapes[selectedShapeIndex].y - 50,
                        left: shapes[selectedShapeIndex].x + shapes[selectedShapeIndex].width - 420
                    }}>X</button>
                )}
            </div>

            <SideToolbar
                onBrightnessChange={setBrightness}
                onContrastChange={setContrast}
                onAddRectangle={() => handleAddShape('rectangle')}
                onTogglePenMode={handleTogglePenMode}
                onToggleRubberMode={handleToggleRubberMode} // Pass handleToggleRubberMode as a prop
                onPenColorChange={setPenColor}
                onPenThicknessChange={setPenThickness}
                penMode={penMode}
                rubberMode={rubberMode}
            />
        </div>
    );
}

export default ImageViewer;
