import React, { useRef, useEffect, useState } from 'react';
import './ImageEditor.css';
import ImageViewer from '../ImageViewer/ImageViewer';

function ImageEditor({ images }) {
    const containerRef = useRef(null);
    const [containerWidth, setContainerWidth] = useState(0);
    const [containerHeight, setContainerHeight] = useState(0);
    const [maxImageSize, setMaxImageSize] = useState(0);
    const [selectedImageIndex, setSelectedImageIndex] = useState(null);

    useEffect(() => {
        const updateContainerSize = () => {
            const container = containerRef.current;
            if (container) {
                setContainerWidth(container.offsetWidth);
                setContainerHeight(container.offsetHeight);
            }
        };

        window.addEventListener('resize', updateContainerSize);
        updateContainerSize();

        return () => {
            window.removeEventListener('resize', updateContainerSize);
        };
    }, []);

    useEffect(() => {
        const calculateMaxImageSize = () => {
            const numImages = images.length;
            const rowImages = Math.floor(containerWidth / 300); // Adjust 300 as needed for desired image width
            const colImages = Math.ceil(numImages / rowImages);
            const width = containerWidth / rowImages;
            const height = containerHeight / colImages;
            return Math.min(width, height);
        };

        setMaxImageSize(calculateMaxImageSize());
    }, [containerWidth, containerHeight, images]);

    const handleImageClick = (index) => {
        setSelectedImageIndex(index);
    };

    const handleCloseViewer = () => {
        setSelectedImageIndex(null);
    };

    return (
        <div className="image-editor" ref={containerRef}>
            {images.map((image, index) => (
                <div key={index} className="image-container" style={{ width: maxImageSize, height: maxImageSize }}>
                    <img
                        src={URL.createObjectURL(image)}
                        alt={`Image ${index}`}
                        className="image-item"
                        onClick={() => handleImageClick(index)}
                    />
                </div>
            ))}
            {selectedImageIndex !== null && (
                <ImageViewer
                    image={images[selectedImageIndex]}
                    onClose={handleCloseViewer}
                />
            )}
        </div>
    );
}

export default ImageEditor;
