import React, { useRef, useEffect, useState } from 'react';
import './ImageViewer.css';
import SideToolbar from '../SideToolbar/SideToolbar';

function ImageViewer({ image, onClose, onSave }) {
    const imageCanvasRef = useRef(null);
    const drawingCanvasRef = useRef(null);
    const [imageContext, setImageContext] = useState(null);
    const [drawingContext, setDrawingContext] = useState(null);
    const [imageSrc, setImageSrc] = useState(null);
    const [brightness, setBrightness] = useState(100);
    const [contrast, setContrast] = useState(100);
    const [shapes, setShapes] = useState([]);
    const [selectedShapeIndex, setSelectedShapeIndex] = useState(null);
    const [isDragging, setIsDragging] = useState(false);
    const [dragStartX, setDragStartX] = useState(0);
    const [dragStartY, setDragStartY] = useState(0);
    const [isResizing, setIsResizing] = useState(false);
    const [resizeIndex, setResizeIndex] = useState(-1);
    const [resizeStartX, setResizeStartX] = useState(0);
    const [resizeStartY, setResizeStartY] = useState(0);
    const [prevMouseX, setPrevMouseX] = useState(0);
    const [prevMouseY, setPrevMouseY] = useState(0);

    const [penMode, setPenMode] = useState(false);
    const [rubberMode, setRubberMode] = useState(false);
    const [penColor, setPenColor] = useState('#000000');
    const [penThickness, setPenThickness] = useState(2);

    useEffect(() => {
        if (image) {
            const reader = new FileReader();
            reader.onload = () => {
                setImageSrc(reader.result);
            };
            reader.readAsDataURL(image);
        }
    }, [image]);

    useEffect(() => {
        const imageCanvas = imageCanvasRef.current;
        const ctx = imageCanvas.getContext('2d');
        setImageContext(ctx);
    }, []);

    useEffect(() => {
        const drawingCanvas = drawingCanvasRef.current;
        const ctx = drawingCanvas.getContext('2d');
        setDrawingContext(ctx);
    }, []);

    useEffect(() => {
        if (imageContext && imageSrc) {
            const img = new Image();
            img.onload = () => {
                imageContext.filter = `brightness(${brightness}%) contrast(${contrast}%)`;
                imageContext.drawImage(img, 0, 0, img.width, img.height);
            };
            img.src = imageSrc;
        }
    }, [imageContext, imageSrc, brightness, contrast]);

    const handleMouseDown = (e) => {
        const canvas = drawingCanvasRef.current;
        const rect = canvas.getBoundingClientRect();
        const mouseX = e.clientX - rect.left;
        const mouseY = e.clientY - rect.top;

        if (penMode || rubberMode) {
            setIsDragging(true);
            setPrevMouseX(mouseX);
            setPrevMouseY(mouseY);
        } else {
            let shapeSelected = false;
            shapes.forEach((shape, index) => {
                if (isPointInsideShape(mouseX, mouseY, shape)) {
                    setSelectedShapeIndex(index);
                    setIsDragging(true);
                    setDragStartX(mouseX - shape.x);
                    setDragStartY(mouseY - shape.y);
                    setIsResizing(isResizingHandle(mouseX, mouseY, shape));
                    if (isResizing) {
                        setResizeIndex(index);
                        setResizeStartX(mouseX - shape.x);
                        setResizeStartY(mouseY - shape.y);
                    }
                    shapeSelected = true;
                }
            });

            if (!shapeSelected) {
                setSelectedShapeIndex(null);
            }
        }
    };

    const handleMouseMove = (e) => {
        if (!isDragging || (!penMode && !rubberMode)) return;

        const canvas = drawingCanvasRef.current;
        const rect = canvas.getBoundingClientRect();
        const mouseX = e.clientX - rect.left;
        const mouseY = e.clientY - rect.top;

        const ctx = drawingContext;

        if (penMode) {
            ctx.strokeStyle = rubberMode ? '#FFFFFF' : penColor;
            ctx.lineWidth = penThickness;
            ctx.lineCap = 'round';
            ctx.beginPath();
            ctx.moveTo(prevMouseX, prevMouseY);
            ctx.lineTo(mouseX, mouseY);
            ctx.stroke();
            ctx.closePath();
            setPrevMouseX(mouseX);
            setPrevMouseY(mouseY);
        } else if (rubberMode) {
            ctx.globalCompositeOperation = 'destination-out';
            ctx.beginPath();
            ctx.arc(mouseX, mouseY, penThickness / 2, 0, 2 * Math.PI);
            ctx.fill();
        } else {
            const newShapes = [...shapes];
            const shape = newShapes[selectedShapeIndex];
            if (!isResizing) {
                shape.x = mouseX - dragStartX;
                shape.y = mouseY - dragStartY;
            } else {
                const dx = mouseX - shape.x - resizeStartX;
                const dy = mouseY - shape.y - resizeStartY;
                shape.width += dx;
                shape.height += dy;
                setResizeStartX(mouseX - shape.x);
                setResizeStartY(mouseY - shape.y);
            }
            setShapes(newShapes);
        }
    };

    const handleMouseUp = () => {
        setIsDragging(false);
        setIsResizing(false);
    };

    const isResizingHandle = (x, y, shape) => {
        return x >= shape.x + shape.width - 10 && x <= shape.x + shape.width &&
            y >= shape.y + shape.height - 10 && y <= shape.y + shape.height;
    };

    const isPointInsideShape = (x, y, shape) => {
        if (shape.type === 'square' || shape.type === 'rectangle') {
            return x >= shape.x && x <= (shape.x + shape.width) &&
                y >= shape.y && y <= (shape.y + shape.height);
        } else if (shape.type === 'circle') {
            const dx = x - shape.x;
            const dy = y - shape.y;
            return dx * dx + dy * dy <= shape.radius * shape.radius;
        }
        return false;
    };

    const handleZoomIn = () => {
        const canvas = imageCanvasRef.current;
        const rect = canvas.getBoundingClientRect();
        const centerX = rect.width / 2;
        const centerY = rect.height / 2;
        const zoomFactor = 1.1;
        const newShapes = shapes.map(shape => {
            if (shape.type === 'square' || shape.type === 'rectangle') {
                shape.x = centerX + (shape.x - centerX) * zoomFactor;
                shape.y = centerY + (shape.y - centerY) * zoomFactor;
                shape.width *= zoomFactor;
                shape.height *= zoomFactor;
            } else if (shape.type === 'circle') {
                shape.x = centerX + (shape.x - centerX) * zoomFactor;
                shape.y = centerY + (shape.y - centerY) * zoomFactor;
                shape.radius *= zoomFactor;
            }
            return shape;
        });
        setShapes(newShapes);
    };

    const handleZoomOut = () => {
        const canvas = imageCanvasRef.current;
        const rect = canvas.getBoundingClientRect();
        const centerX = rect.width / 2;
        const centerY = rect.height / 2;
        const zoomFactor = 0.9;
        const newShapes = shapes.map(shape => {
            shape.x = centerX + (shape.x - centerX) * zoomFactor;
            shape.y = centerY + (shape.y - centerY) * zoomFactor;
            shape.width *= zoomFactor;
            shape.height *= zoomFactor;
            return shape;
        });
        setShapes(newShapes);
    };


    const handleSave = () => {
        const imageCanvas = imageCanvasRef.current;
        const drawingCanvas = drawingCanvasRef.current;

        const mergedCanvas = document.createElement('canvas');
        const mergedCtx = mergedCanvas.getContext('2d');

        // Set the dimensions of the merged canvas
        mergedCanvas.width = Math.max(imageCanvas.width, drawingCanvas.width);
        mergedCanvas.height = Math.max(imageCanvas.height, drawingCanvas.height);

        // Draw the image canvas onto the merged canvas
        mergedCtx.drawImage(imageCanvas, 0, 0);

        // Draw the drawing canvas onto the merged canvas
        mergedCtx.drawImage(drawingCanvas, 0, 0);

        // Convert the merged canvas to a blob and save it
        mergedCanvas.toBlob(blob => {
            onSave(blob);
        }, 'image/jpeg', 1);
    };


    const togglePenMode = () => {
        setPenMode(prevPenMode => !prevPenMode);
        setRubberMode(false); // Ensure rubber mode is turned off when switching to pen mode
    };

    const toggleRubberMode = () => {
        setRubberMode(!rubberMode);
        setPenMode(false); // Ensure pen mode is turned off when switching to rubber mode
    };


    return (
        <div className="image-viewer">
            <div className="canvas-container">
                <canvas
                    ref={imageCanvasRef}
                    className="canvas"
                    style={{ pointerEvents: 'none' }}
                />
                <canvas
                    ref={drawingCanvasRef}
                    className="canvas"
                    onMouseDown={handleMouseDown}
                    onMouseMove={handleMouseMove}
                    onMouseUp={handleMouseUp}
                    style={{ border: '1px solid black' }}
                />
            </div>
            <div className="controls">
                <button onClick={handleZoomIn}>Zoom In</button>
                <button onClick={handleZoomOut}>Zoom Out</button>
                <button onClick={handleSave}>Save</button>
                <button onClick={onClose}>Close</button>
                <button onClick={togglePenMode}>{penMode ? 'Switch to Eraser' : 'Switch to Pen'}</button>
                {selectedShapeIndex !== null && (
                    <button onClick={handleDeleteShape} style={{
                        position: 'absolute',
                        top: shapes[selectedShapeIndex].y - 50,
                        left: shapes[selectedShapeIndex].x + shapes[selectedShapeIndex].width - 420
                    }}>X</button>
                )}
            </div>

            <SideToolbar
                onBrightnessChange={setBrightness}
                onContrastChange={setContrast}
                onAddRectangle={() => handleAddShape('rectangle')}
                onTogglePenMode={togglePenMode}
                onToggleRubberMode={toggleRubberMode} // Pass toggleRubberMode here
                onPenColorChange={setPenColor}
                onPenThicknessChange={setPenThickness}
                penMode={penMode}
                rubberMode={rubberMode}
            />
        </div>
    );
}

export default ImageViewer;
