// ImageViewer.js

import React, { useState } from 'react';
import './ImageViewer.css';

function ImageViewer({ image, onClose }) {
    const [scale, setScale] = useState(1);
    const [positionX, setPositionX] = useState(0);
    const [positionY, setPositionY] = useState(0);

    const handleZoomIn = () => {
        setScale(scale + 0.1);
    };

    const handleZoomOut = () => {
        if (scale > 0.1) {
            setScale(scale - 0.1);
        }
    };

    const handleMouseMove = (e) => {
        setPositionX(e.nativeEvent.offsetX);
        setPositionY(e.nativeEvent.offsetY);
    };

    return (
        <div className="image-viewer" onClick={onClose}>
            <div className="controls">
                <button onClick={handleZoomIn}>Zoom In</button>
                <button onClick={handleZoomOut}>Zoom Out</button>
                <button>Close</button>
            </div>
            <div className="image-container" onMouseMove={handleMouseMove}>
                {image && <img
                    src={URL.createObjectURL(image)}
                    alt="Zoomed Image"
                    className="zoomed-image"
                    style={{
                        transform: `scale(${scale})`,
                        transformOrigin: `${positionX}px ${positionY}px`
                    }}
                />}
            </div>
        </div>
    );
}

export default ImageViewer;
