import React, { useRef, useEffect, useState } from 'react';
import './ImageViewer.css';
import SideToolbar from '../SideToolbar/SideToolbar';
import Cropper from 'react-easy-crop';

function ImageViewer({ image, onClose, onSave }) {
    const canvasRef = useRef(null);
    const [context, setContext] = useState(null);
    const [crop, setCrop] = useState({ x: 0, y: 0 });
    const [zoom, setZoom] = useState(1);
    const [imageSrc, setImageSrc] = useState(null);
    const [brightness, setBrightness] = useState(100);
    const [contrast, setContrast] = useState(100);
    const [shapes, setShapes] = useState([]);
    const [selectedShapeIndex, setSelectedShapeIndex] = useState(null);
    const [isDragging, setIsDragging] = useState(false);
    const [dragStartX, setDragStartX] = useState(0);
    const [dragStartY, setDragStartY] = useState(0);

    useEffect(() => {
        if (image) {
            const reader = new FileReader();
            reader.onload = () => {
                setImageSrc(reader.result);
            };
            reader.readAsDataURL(image);
        }
    }, [image]);

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        setContext(ctx);
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        if (imageSrc && context) {
            const img = new Image();
            img.onload = () => {
                canvas.width = img.width;
                canvas.height = img.height;
                ctx.filter = `brightness(${brightness}%) contrast(${contrast}%)`;
                ctx.drawImage(img, 0, 0);
                shapes.forEach((shape, index) => {
                    drawShape(ctx, shape, index === selectedShapeIndex);
                });
            };
            img.src = imageSrc;
        }
    }, [imageSrc, context, brightness, contrast, shapes, selectedShapeIndex]);

    const drawShape = (ctx, shape, isSelected) => {
        ctx.beginPath();
        if (shape.type === 'square' || shape.type === 'rectangle') {
            ctx.rect(shape.x, shape.y, shape.width, shape.height);
        } else if (shape.type === 'circle') {
            ctx.arc(shape.x, shape.y, shape.radius, 0, 2 * Math.PI);
        }
        ctx.strokeStyle = isSelected ? 'red' : shape.color;
        ctx.lineWidth = shape.lineWidth;
        ctx.stroke();
    };

    const handleSave = () => {
        const canvas = canvasRef.current;
        canvas.toBlob(blob => {
            onSave(blob);
        }, 'image/jpeg', 1);
    };

    const handleAddSquare = () => {
        const newShapes = [...shapes];
        newShapes.push({
            type: 'square',
            x: 100,
            y: 100,
            width: 50,
            height: 50,
            color: 'red',
            lineWidth: 2,
        });
        setShapes(newShapes);
    };

    const handleAddRectangle = () => {
        const newShapes = [...shapes];
        newShapes.push({
            type: 'rectangle',
            x: 200,
            y: 200,
            width: 80,
            height: 40,
            color: 'blue',
            lineWidth: 2,
        });
        setShapes(newShapes);
    };

    const handleAddCircle = () => {
        const newShapes = [...shapes];
        newShapes.push({
            type: 'circle',
            x: 300,
            y: 300,
            radius: 30,
            color: 'green',
            lineWidth: 2,
        });
        setShapes(newShapes);
    };

    const handleMouseDown = (e) => {
        const canvas = canvasRef.current;
        const rect = canvas.getBoundingClientRect();
        const mouseX = e.clientX - rect.left;
        const mouseY = e.clientY - rect.top;

        shapes.forEach((shape, index) => {
            if (isPointInsideShape(mouseX, mouseY, shape)) {
                setSelectedShapeIndex(index);
                setIsDragging(true);
                setDragStartX(mouseX - shape.x);
                setDragStartY(mouseY - shape.y);
            }
        });
    };

    const handleMouseMove = (e) => {
        if (!isDragging || selectedShapeIndex === null) return;

        const canvas = canvasRef.current;
        const rect = canvas.getBoundingClientRect();
        const mouseX = e.clientX - rect.left;
        const mouseY = e.clientY - rect.top;

        const newShapes = [...shapes];
        const shape = newShapes[selectedShapeIndex];
        if (shape.type === 'square' || shape.type === 'rectangle') {
            shape.x = mouseX - dragStartX;
            shape.y = mouseY - dragStartY;
        } else if (shape.type === 'circle') {
            shape.x = mouseX;
            shape.y = mouseY;
        }
        setShapes(newShapes);
    };

    const handleMouseUp = () => {
        setIsDragging(false);
    };

    const isPointInsideShape = (x, y, shape) => {
        if (shape.type === 'square' || shape.type === 'rectangle') {
            return x >= shape.x && x <= shape.x + shape.width && y >= shape.y && y <= shape.y + shape.height;
        } else if (shape.type === 'circle') {
            const dx = x - shape.x;
            const dy = y - shape.y;
            return dx * dx + dy * dy <= shape.radius * shape.radius;
        }
        return false;
    };

    return (
        <div className="image-viewer">
            <canvas
                ref={canvasRef}
                onMouseDown={handleMouseDown}
                onMouseMove={handleMouseMove}
                onMouseUp={handleMouseUp}
            />
            <SideToolbar
                onBrightnessChange={setBrightness}
                onContrastChange={setContrast}
                onSave={handleSave}
                onAddSquare={handleAddSquare}
                onAddRectangle={handleAddRectangle}
                onAddCircle={handleAddCircle}
            />
        </div>
    );
}

export default ImageViewer;
