import React, { useState } from 'react';
import './ImageViewer.css';

function ImageViewer({ image, onClose }) {
    const [scale, setScale] = useState(1);
    const [positionX, setPositionX] = useState(0);
    const [positionY, setPositionY] = useState(0);
    const [isDragging, setIsDragging] = useState(false);
    const [dragStartX, setDragStartX] = useState(0);
    const [dragStartY, setDragStartY] = useState(0);

    const handleZoomIn = () => {
        setScale(scale + 0.1);
    };

    const handleZoomOut = () => {
        if (scale > 0.1) {
            setScale(scale - 0.1);
        }
    };

    const handleMouseMove = (e) => {
        if (!isDragging) return;
        setPositionX(e.clientX - dragStartX);
        setPositionY(e.clientY - dragStartY);
    };

    const stopPropagation = (e) => {
        e.stopPropagation();
    };

    const handleMouseUp = () => {
        if (!isDragging) return; // Ignore if not dragging
        setIsDragging(false);
    };


    document.addEventListener('mouseup', handleMouseUp); // Add mouseup event listener

    document.addEventListener('mousemove', handleMouseMove); // Add mousemove event listener

    document.removeEventListener('mouseup', handleMouseUp); // Remove mouseup event listener

    const handleMouseDown = (e) => {
        setIsDragging(true);
        setDragStartX(e.clientX - positionX);
        setDragStartY(e.clientY - positionY);
    };

    return (
        <div className="image-viewer" onClick={stopPropagation}>
            <div className="controls">
                <button onClick={handleZoomIn}>Zoom In</button>
                <button onClick={handleZoomOut}>Zoom Out</button>
                <button onClick={onClose}>Close</button>
            </div>
            <div
                className="image-container"
                onMouseDown={handleMouseDown}
                onMouseMove={handleMouseMove}
                onMouseUp={handleMouseUp}
                style={{ overflow: isDragging ? 'hidden' : 'visible' }} // Hide overflow when dragging
            >
                {image && (
                    <img
                        src={URL.createObjectURL(image)}
                        alt="Zoomed Image"
                        className="zoomed-image"
                        style={{
                            transform: `scale(${scale}) translate(${positionX}px, ${positionY}px)`,
                            cursor: isDragging ? 'grabbing' : 'grab',
                            visibility: isDragging ? 'hidden' : 'visible' // Hide image during dragging
                        }}
                    />
                )}
            </div>
        </div>
    );
}

export default ImageViewer;
