import React, { useState } from 'react';
import './SideToolbar.css';

function SideToolbar({ onBrightnessChange, onContrastChange, onAddRectangle, onAddCircle, onPenModeChange, onRubberModeChange, onPenColorChange, onPenThicknessChange }) {
    const [penMode, setPenMode] = useState(false);
    const [rubberMode, setRubberMode] = useState(false);
    const [penColor, setPenColor] = useState('#000000');
    const [penThickness, setPenThickness] = useState(2);

    const handleBrightnessInputChange = (e) => {
        const value = parseInt(e.target.value);
        onBrightnessChange(value);
    };

    const handleContrastInputChange = (e) => {
        const value = parseInt(e.target.value);
        onContrastChange(value);
    };


    const handlePenColorChange = (e) => {
        const color = e.target.value;
        onPenColorChange(color); // Pass the selected color to the parent component
    };

    const handlePenThicknessChange = (e) => {
        const thickness = parseInt(e.target.value);
        onPenThicknessChange(thickness); // Pass the selected thickness to the parent component
    };


    const handlePenModeToggle = () => {
        const newPenMode = !penMode;
        setPenMode(newPenMode);
        onPenModeChange(newPenMode);
        if (newPenMode && rubberMode) {
            setRubberMode(false);
            onRubberModeChange(false);
        }
    };

    const handleRubberModeToggle = () => {
        const newRubberMode = !rubberMode;
        setRubberMode(newRubberMode);
        onRubberModeChange(newRubberMode);
        if (newRubberMode && penMode) {
            setPenMode(false);
            onPenModeChange(false);
        }
    };

    return (
        <div className="side-toolbar">
            <div>
                <label htmlFor="brightness">Brightness:</label>
                <input
                    type="range"
                    id="brightness"
                    min="0"
                    max="200"
                    defaultValue="100"
                    onChange={handleBrightnessInputChange}
                />
            </div>
            <div>
                <label htmlFor="contrast">Contrast:</label>
                <input
                    type="range"
                    id="contrast"
                    min="0"
                    max="200"
                    defaultValue="100"
                    onChange={handleContrastInputChange}
                />
            </div>
            <div>
                <button onClick={onAddRectangle}>Add Rectangle</button>
            </div>
            <div>
                <label htmlFor="penColor">Pen Color:</label>
                <input
                    type="color"
                    id="penColor"
                    value={penColor}
                    onChange={handlePenColorChange}
                />
            </div>
            <div>
                <label htmlFor="penThickness">Pen Thickness:</label>
                <input
                    type="number"
                    id="penThickness"
                    min="1"
                    max="10"
                    value={penThickness}
                    onChange={handlePenThicknessChange}
                />
            </div>
            <div>
                <button onClick={handlePenModeToggle}>{penMode ? 'Disable Pen Mode' : 'Enable Pen Mode'}</button>
            </div>
            <div>
                <button onClick={handleRubberModeToggle}>{rubberMode ? 'Disable Rubber Mode' : 'Enable Rubber Mode'}</button>
            </div>
        </div>
    );
}

export default SideToolbar;
