import React from 'react';
import './ImageEditor.css';

function ImageEditor({ images, onImageClick }) {
    const getImageSize = () => {
        const numImages = images.length;
        const viewportWidth = window.innerWidth;
        const viewportHeight = window.innerHeight;
        const maxSize = Math.min(viewportWidth, viewportHeight) / Math.ceil(Math.sqrt(numImages));
        return maxSize;
    };

    return (
        <div className="image-editor">
            {images.map((image, index) => (
                <div key={index} className="image-container" onClick={() => onImageClick(index)}>
                    <img
                        src={URL.createObjectURL(image)}
                        alt={`Image ${index}`}
                        className="image-item"
                        style={{ maxWidth: getImageSize(), maxHeight: getImageSize() }}
                    />
                </div>
            ))}
        </div>
    );
}

export default ImageEditor;
