import React from 'react';
import './ImageEditor.css';

function ImageEditor({ images }) {
    const containerRef = React.useRef(null);

    const calculateGridTemplateColumns = () => {
        const containerWidth = containerRef.current.offsetWidth;
        const numColumns = Math.floor(containerWidth / 300); // Adjust 300 as needed for desired image width
        return `repeat(auto-fit, minmax(0, 1fr))`; // Adjust as needed
    };

    return (
        <div className="image-editor" ref={containerRef}>
            {images.map((image, index) => (
                <div key={index} className="image-container">
                    <img
                        src={URL.createObjectURL(image)}
                        alt={`Image ${index}`}
                        className="image-item"
                    />
                </div>
            ))}
        </div>
    );
}

export default ImageEditor;
