import React, { useState } from 'react';
import './App.css';
import UpToolbar from "../UpToolbar/UpToolbar";
import ImageEditor from "../ImageEditor/ImageEditor";
import ImageViewer from "../ImageViewer/ImageViewer";

function App() {
    const [selectedImages, setSelectedImages] = useState([]);
    const [selectedImageIndex, setSelectedImageIndex] = useState(null);

    const handleFilesSelect = (files) => {
        // Log selected files to ensure they are correctly received
        console.log('Selected files:', files);

        setSelectedImages(files);
        setSelectedImageIndex(null); // Reset selected image index when new images are selected
    };

    const handleImageClick = (index) => {
        setSelectedImageIndex(index); // Set the selected image index when an image is clicked
    };

    const handleCloseViewer = () => {
        setSelectedImageIndex(null); // Close the ImageViewer by resetting the selected image index
    };

    // Log selected images to ensure they are correctly received and updated
    console.log('Selected images:', selectedImages);

    const handleSaveImage = (editedImage) => {
        // Update the selectedImages array with the edited image
        const updatedImages = [...selectedImages];
        updatedImages[selectedImageIndex] = editedImage;
        setSelectedImages(updatedImages);
    };

    return (
        <div className="App">
            <UpToolbar onFilesSelect={handleFilesSelect} />
            {/* Pass selectedImages and handleImageClick to ImageEditor */}
            <ImageEditor images={selectedImages} onImageClick={handleImageClick} />
            {selectedImageIndex !== null && (
                <ImageViewer
                    image={selectedImages[selectedImageIndex]}
                    onClose={handleCloseViewer}
                    onSave={handleSaveImage}
                />
            )}
        </div>
    );
}

export default App;
