import React, { useRef, useEffect, useState } from 'react';
import './ImageViewer.css';

function ImageViewer({ image, onClose, onSave }) {
    const canvasRef = useRef(null);
    const [context, setContext] = useState(null);
    const [isDrawing, setIsDrawing] = useState(false);
    const [startX, setStartX] = useState(0);
    const [startY, setStartY] = useState(0);
    const [endX, setEndX] = useState(0);
    const [endY, setEndY] = useState(0);
    const [shapes, setShapes] = useState([]);

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        setContext(ctx);
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        if (image && image instanceof HTMLImageElement) { // Check if image is truthy and of type HTMLImageElement
            ctx.drawImage(image, 0, 0, canvas.width, canvas.height);
        }
    }, [image]);

    const handleMouseDown = (e) => {
        setIsDrawing(true);
        setStartX(e.nativeEvent.offsetX);
        setStartY(e.nativeEvent.offsetY);
    };

    const handleMouseMove = (e) => {
        if (!isDrawing) return;
        setEndX(e.nativeEvent.offsetX);
        setEndY(e.nativeEvent.offsetY);
    };

    const handleMouseUp = () => {
        setIsDrawing(false);
        // Save the drawn shape
        setShapes([...shapes, { startX, startY, endX, endY }]);
    };

    const drawShapes = () => {
        if (!context) return;
        context.strokeStyle = 'red';
        context.lineWidth = 2;
        shapes.forEach(({ startX, startY, endX, endY }) => {
            const width = endX - startX;
            const height = endY - startY;
            context.strokeRect(startX, startY, width, height);
        });
    };

    useEffect(() => {
        drawShapes();
    }, [shapes]);

    const handleSave = () => {
        onSave(canvasRef.current.toDataURL());
    };

    return (
        <div className="image-viewer-container">
            <canvas
                ref={canvasRef}
                className="image-canvas"
                onMouseDown={handleMouseDown}
                onMouseMove={handleMouseMove}
                onMouseUp={handleMouseUp}
            />
            <div className="image-viewer-buttons">
                <button onClick={onClose}>Close</button>
                <button onClick={handleSave}>Save</button>
            </div>
        </div>
    );
}

export default ImageViewer;
