import React from 'react';
import "./SideToolbar.css"

function SideToolbar({ onBrightnessChange, onContrastChange, onSave, applyFilters }) {
    const handleBrightnessChange = (e) => {
        const value = parseInt(e.target.value);
        onBrightnessChange(e); // Call the brightness change handler in the parent component
        applyFilters(); // Apply filters after changing brightness
    };

    const handleContrastChange = (e) => {
        const value = parseInt(e.target.value);
        onContrastChange(e); // Call the contrast change handler in the parent component
        applyFilters(); // Apply filters after changing contrast
    };

    return (
        <div className="side-toolbar">
            <div>
                <label htmlFor="brightness">Brightness:</label>
                <input type="range" id="brightness" min="-100" max="100" defaultValue="0" onChange={handleBrightnessChange} />
            </div>
            <div>
                <label htmlFor="contrast">Contrast:</label>
                <input type="range" id="contrast" min="-100" max="100" defaultValue="0" onChange={handleContrastChange} />
            </div>
            <button onClick={onSave}>Save</button>
        </div>
    );
}

export default SideToolbar;
