import React, { useRef, useEffect, useState } from 'react';
import './ImageEditor.css';

function ImageEditor({ images, onImageClick }) {
    const containerRef = useRef(null);
    const [containerWidth, setContainerWidth] = useState(0);
    const [containerHeight, setContainerHeight] = useState(0);
    const [maxImageHeight, setMaxImageHeight] = useState(0);

    useEffect(() => {
        const updateContainerSize = () => {
            const container = containerRef.current;
            if (container) {
                setContainerWidth(container.offsetWidth);
                setContainerHeight(container.offsetHeight);
            }
        };

        window.addEventListener('resize', updateContainerSize);
        updateContainerSize();

        return () => {
            window.removeEventListener('resize', updateContainerSize);
        };
    }, []);

    useEffect(() => {
        if (images.length === 0) return;

        const maxAspectRatio = Math.max(...images.map(image => image.width / image.height));
        const maxHeight = containerHeight / Math.ceil(Math.sqrt(images.length));
        setMaxImageHeight(maxHeight * maxAspectRatio);
    }, [containerHeight, images]);

    return (
        <div className="image-editor" ref={containerRef}>
            {images.map((image, index) => (
                <div key={index} className="image-container" onClick={() => onImageClick(index)}>
                    <img
                        src={URL.createObjectURL(image)}
                        alt={`Image ${index}`}
                        className="image-item"
                        style={{ maxHeight: maxImageHeight }}
                    />
                </div>
            ))}
        </div>
    );
}

export default ImageEditor;
